import { connect } from 'react-redux';
import { TasksList } from './tasksList.component';
import { getTasks } from '../tasks.selectors';
import { fetchTasksAction, removeTaskAction } from '../tasks.actions';

const mapStateToProps = function (state) {
  return {
    tasks: getTasks(state)
  };
};

const mapDispatchToProps = function (dispatch) {
  return {
    fetchTasks: function () {
      return dispatch(fetchTasksAction());
    },
    removeTask: function (taskId) {
      return dispatch(removeTaskAction(taskId));
    }
  };
};

export const ConnectedTasksList = connect(mapStateToProps, mapDispatchToProps)(TasksList);
