import _ from 'lodash';
export const getPreview = function ({
  school
}) {
  return _.get(school, 'preview');
};
export const getSchoolYear = function ({
  school
}) {
  return _.get(school, 'schoolYear');
};
export const getClasse = function ({
  school
}, classeId) {
  return _.find(_.get(school, 'classes', []), function (classe) {
    return classe._id === classeId;
  });
};
