import { combineEpics } from 'redux-observable';
import { SHOW_TOASTER } from './display.constants';
import { hideToaster } from './display.actions';

var hideToasterEpic = function hideToasterEpic(action$) {
  return action$.filter(function (action) {
    return action.type === SHOW_TOASTER;
  }).delay(1000).map(function () {
    return hideToaster();
  });
};

export default combineEpics(hideToasterEpic);
