import _ from 'lodash';
import moment from 'moment';
moment.locale('fr');
export const getWeeks = function ({
  calendar
}) {
  return _.get(calendar, 'weeks');
};
export const getCurrentWeek = function ({
  calendar
}, date) {
  const immutableWeeks = _.get(calendar, 'weeks');

  if (immutableWeeks) {
    return immutableWeeks.get(date || _.get(calendar, 'selectedWeek').format('YYYY.MM.DD'));
  }
};
export const getDates = function ({
  calendar
}) {
  return _.get(calendar, 'dates');
};
export const getSelectedWeek = function ({
  calendar
}, weekId) {
  if (weekId) {
    return moment(weekId, 'YYYY.MM.DD').startOf('week');
  }

  return _.get(calendar, 'selectedWeek');
};
