import _ from 'lodash';
import firebase from '../../config/firebase';
import { SIGNIN, SIGNOUT, AUTO_LOGIN_DONE } from './account.constants';
import { showToaster } from '../display/display.actions';
export var login = function login(user) {
  return {
    type: SIGNIN,
    user: _.pick(user, ['email', 'uid', 'refreshToken'])
  };
};
export var logout = function logout() {
  return {
    type: SIGNOUT,
    user: null
  };
};
export var autoLogin = function autoLogin() {
  return function (dispatch) {
    var stopObserver = firebase.auth().onAuthStateChanged(function (user) {
      if (user) {
        dispatch(login(user));
      }

      dispatch(showToaster('La connexion automatique à fini'));
      dispatch({
        type: AUTO_LOGIN_DONE
      });
      stopObserver();
    });
  };
};
export function signIn(email, password) {
  return function (dispatch) {
    firebase.auth().signInWithEmailAndPassword(email, password).then(function (user) {
      dispatch(login(user));
      dispatch(showToaster('Connexion reussi'));
    });
  };
}
export function signOut() {
  return function (dispatch) {
    firebase.auth().signOut().then(function (error) {
      if (!error) {
        dispatch(logout());
        dispatch(showToaster('Déconnection réussi'));
      }
    });
  };
}
