import { SHOW_TOASTER, HIDE_TOASTER } from './display.constants';
export const showToaster = function (message, style = 'default', timeout = 3000) {
  return {
    type: SHOW_TOASTER,
    message,
    style,
    timeout
  };
};
export const hideToaster = function () {
  return {
    type: HIDE_TOASTER
  };
};
